const dictionary = require('./jedi-validate-i18n-data.json');

/**
 * Default language
 * @type {string}
 */
const defaultLanguage = 'en';

/**
 * Translate phrase
 * @param {string} text - phrase to translate
 * @param {string} language - language token
 * @returns {string} - translated text
 */
export function translate(text, language = defaultLanguage) {
    return (dictionary[language] && dictionary[language][text]) || text;
}

/**
 * Add translation pair to dictionary
 * @param {string} sourceText - phrase
 * @param {string} translatedText - translated phrase
 * @param {string} language - language token
 */
export function addTranslation(sourceText, translatedText, language = defaultLanguage) {
    if (dictionary[language] === undefined) {
        dictionary[language] = {};
    }

    dictionary[language][sourceText] = translatedText;
}

import deepmerge from 'deepmerge';
import { getData, getInputData, getValueByName } from './lib/get-data';
import { convertData } from './lib/convert-data';
import { addTranslation, translate } from './i18n/jedi-validate-i18n';
import { getFormOptions, getInputRules } from './lib/get-options';
import { validateData, validateField } from './lib/validate-data';
import { ajax } from './lib/ajax';
import defaultMethods from './lib/methods';

class JediValidate {
    /**
     * Object with fields
     * @type {Object.<string, Element>}
     */
    fields = {};
    /**
     * Object with inputs nodes
     * @type {Object.<string, HTMLInputElement|HTMLSelectElement|Array>}
     */
    inputs = {};
    /**
     * Object with message nodes
     * @type {Object.<string, Element>}
     */
    messages = {};
    /**
     * Object with error message
     * @type {Object.<string, Object.<string, string>>}
     */
    errorMessages = {};
    /**
     * Object with error message
     * @type {object} - data object
     */
    data = {};
    /**
     * Validate methods
     * @type {Object.<string, {func: Function, message: string}>}
     */
    methods = { ...defaultMethods };
    /**
     * Validator options
     * @type {{ajax: {url: string, enctype: string, sendType: string, method: string}, rules: {}, messages: {}, containers: {parent: string, message: string, baseMessage: string}, states: {error: string, valid: string, pristine: string, dirty: string}, formStatePrefix: string, callbacks: {success: (function(object)), error: (function(object.<string, Array.<string>>))}, clean: boolean, redirect: boolean, language: string, translations: {}}}
     */
    options = {};
    /**
     * Validator rules
     * @type {object}
     */
    rules = {};

    /**
     * JediValidate
     * @param {HTMLElement} root - element which wrap form element
     * @param {object} options - object with options
     */
    constructor(root, options = {}) {
        const defaultOptions = {
            ajax: {
                url: null,
                enctype: 'application/x-www-form-urlencoded',
                sendType: 'serialize', // 'serialize', 'formData', 'json'
                method: 'GET',
            },
            rules: {},
            messages: {},
            containers: {
                parent: 'form-group',
                message: 'help-block',
                baseMessage: 'base-error',
            },
            states: {
                error: 'error',
                valid: 'valid',
                pristine: 'pristine',
                dirty: 'dirty',
            },
            formStatePrefix: 'jedi-',
            callbacks: {
                success({event, response}) {
                },
                error({errors}) {
                },
            },
            clean: true,
            redirect: true,
            language: 'en',
            translations: {},
        };

        this.root = root;

        this.options = deepmerge(defaultOptions, options);

        this.nodes = JediValidate.cacheNodes(this.root, this.options);

        const formOptions = getFormOptions(this.nodes.form);

        this.options = deepmerge(this.options, defaultOptions);
        this.options = deepmerge(this.options, formOptions);
        this.options = deepmerge(this.options, options);

        this.rules = { ...this.options.rules };

        // todo rewrite translations
        Object.keys(this.options.translations).forEach((language) => {
            Object.keys(this.options.translations[language]).forEach((translation) => {
                addTranslation(
                    translation,
                    this.options.translations[language][translation],
                    language,
                );
            });
        });

        this.ready();

        this.errorMessages = JediValidate.initErrorMessages(
            this.rules,
            this.options.messages,
            this.methods,
            this.options.language,
        );
    }

    /**
     * Add localisation to JediValidate
     * @param {string} sourceText - text on english
     * @param {string} translatedText - text on needed language
     * @param {string} language - language
     */
    static addToDictionary(sourceText, translatedText, language) {
        addTranslation(sourceText, translatedText, language);
    }

    /**
     * Return object with working elements
     * @param root Root element for search
     * @param options Object with selectors
     * @returns {{form: HTMLFormElement, inputs: NodeList, baseMessage: Element}}
     */
    static cacheNodes(root, options) {
        return {
            form: root.querySelector('form'),
            inputs: root.querySelectorAll('form [name]'),
            baseMessage: root.querySelector(`.${options.containers.baseMessage}`),
        };
    }

    ready() {
        this.nodes.form.setAttribute('novalidate', 'novalidate');

        this.nodes.form.addEventListener('submit', (event) => {
            this.data = getData(this.inputs);

            const errors = validateData(
                this.rules,
                this.methods,
                this.data,
                this.errorMessages,
            );

            if (errors && Object.keys(errors).filter(name => errors[name]).length !== 0) {
                Object.keys(errors).forEach(name =>
                    JediValidate.markField(
                        this.fields[name],
                        this.messages[name],
                        this.options.states,
                        errors[name],
                    ),
                );

                try {
                    this.options.callbacks.error({errors});
                } catch (e) {
                    console.error(e);
                }

                event.preventDefault();
                return;
            }

            if (this.options.ajax && this.options.ajax.url) {
                event.preventDefault();
            } else {
                try {
                    this.options.callbacks.success({event});
                } catch (e) {
                    console.error(e);
                }

                return;
            }

            const convertedData = convertData(this.data, this.options.ajax.sendType);
            this.send({
                ...this.options.ajax,
                data: convertedData,
            });
        });

        this.nodes.inputs.forEach((input) => {
            // fixme "name" and "name in data" not the same
            // name === "phone[]",
            // data: { phone: [] } - name === "phone"
            const name = input.name;

            if (this.inputs[name]) {
                if (Array.isArray(this.inputs[name])) {
                    this.inputs[name].push(input);
                } else {
                    const groupInput = [this.inputs[name], input];
                    groupInput.name = name;
                    this.inputs[name] = groupInput;
                }
            } else {
                this.inputs[name] = input;

                let field = input.parentNode;

                do {
                    if (field.classList.contains(this.options.containers.parent)) {
                        this.fields[name] = field;
                        break;
                    }

                    field = field.parentNode;
                } while (field && field.classList);

                if (!this.fields[name]) {
                    console.warn(`Input ${name} has no parent field`);
                    delete this.inputs[name];
                    return;
                }

                this.fields[name].classList.add(this.options.states.pristine);

                const messageElement = this.fields[name].querySelector(`.${this.options.containers.message}`);

                if (messageElement) {
                    this.messages[name] = messageElement;
                } else {
                    this.messages[name] = document.createElement('div');
                    this.messages[name].classList.add(this.options.containers.message);
                    this.fields[name].appendChild(this.messages[name]);
                }

                this.rules[name] = this.rules[name] || {};
                const inputRules = getInputRules(input);
                this.rules[name] = deepmerge(inputRules, this.rules[name]);

                Object.keys(this.rules[name]).forEach((rule) => {
                    if (this.rules[name][rule]) {
                        this.fields[name].classList.add(rule);
                    }
                });
            }

            input.addEventListener('change', () => {
                this.fields[name].classList.remove(this.options.states.dirty);

                const inputData = getInputData(input);
                const value = getValueByName(name, inputData);

                // fixme don't work with 2 inputs phone[]
                this.data = {
                    ...this.data,
                    ...inputData,
                };

                const errors = validateField(
                    this.rules[name],
                    this.methods,
                    value,
                    name,
                    this.errorMessages,
                    this.data,
                );

                JediValidate.markField(
                    this.fields[name],
                    this.messages[name],
                    this.options.states,
                    errors,
                );
            });

            input.addEventListener('input', () => {
                this.fields[name].classList.remove(this.options.states.pristine);
                this.fields[name].classList.add(this.options.states.dirty);
            });
        });
    }

    /**
     * Send form
     * @param {object} options - object with options for sending
     * @param {string} options.url
     * @param {string} options.enctype
     * @param {string} options.sendType
     * @param {string} options.method
     * @param {string|FormData} options.data
     */
    send(options) {
        ajax(options).then((response) => {
            if (response.validationErrors) {
                try {
                    this.options.callbacks.error({errors: response.validationErrors});
                } catch (e) {
                    console.error(e);
                }

                if (response.validationErrors.base) {
                    this.nodes.baseMessage.innerHTML = response.validationErrors.base.join(', ');
                    this.root.classList.add(this.options.formStatePrefix + this.options.states.error); // eslint-disable-line max-len
                    this.root.classList.remove(this.options.formStatePrefix + this.options.states.valid); // eslint-disable-line max-len
                    delete response.validationErrors.base; // eslint-disable-line no-param-reassign
                } else {
                    this.nodes.baseMessage.innerHTML = '';
                }

                Object.keys(response.validationErrors).forEach(name =>
                    JediValidate.markField(
                        this.fields[name],
                        this.messages[name],
                        this.options.states,
                        response.validationErrors[name],
                    ),
                );
            } else {
                try {
                    this.options.callbacks.success({response});
                } catch (e) {
                    console.error(e);
                }

                if (this.options.redirect && response.redirect) {
                    window.location.href = response.redirect;
                    return;
                }

                if (this.options.clean) {
                    this.nodes.form.reset();
                }
            }
        }).catch(({ method, url, status, statusText }) => {
            console.warn(`${method} ${url} ${status} (${statusText})`);

            this.nodes.baseMessage.innerHTML = translate('Can not send form!', this.options.language);
            this.root.classList.add(this.options.formStatePrefix + this.options.states.error); // eslint-disable-line max-len
            this.root.classList.remove(this.options.formStatePrefix + this.options.states.valid); // eslint-disable-line max-len
        });
    }


    /**
     * Collect data
     * @param {string} name - field name
     */
    collect(name = '') {
        if (name) {
            const inputData = getInputData(this.inputs[name]);

            // fixme don't work with 2 inputs phone[]
            this.data = {
                ...this.data,
                ...inputData,
            };
        } else {
            this.data = getData(this.inputs);
        }

        return this.data;
    }

    /**
     *
     * @param {Element} field
     * @param message
     * @param states
     * @param errors
     */
    static markField(field, message, states, errors) {
        if (errors && errors.length) {
            JediValidate.markError(field, message, states, errors);
        } else {
            JediValidate.markValid(field, message, states);
        }
    }

    /**
     *
     * @param {Element} field
     * @param {Element} message
     * @param {string} error
     * @param {string} valid
     * @param {Array.<string>} errors
     */
    static markError(field, message, { error, valid }, errors) {
        if (!field || !message) {
            return;
        }

        field.classList.add(error);
        field.classList.remove(valid);

        message.innerHTML = errors.join(', '); // eslint-disable-line no-param-reassign
    }

    /**
     *
     * @param {Element} field
     * @param {Element} message
     * @param {string} error
     * @param {string} valid
     */
    static markValid(field, message, { error, valid }) {
        if (!field || !message) {
            return;
        }

        field.classList.add(valid);
        field.classList.remove(error);

        message.innerHTML = ''; // eslint-disable-line no-param-reassign
    }

    /**
     * Add rule to validator
     * @param {string} rule - rule name
     * @param {Function} func - function
     * @param {string} message - error message
     */
    addMethod(rule, func, message) {
        this.methods[rule] = {
            func,
            message,
        };
    }

    /**
     * Init error messages
     * @param {object} rules
     * @param {object} messages
     * @param {object} methods
     * @param {string} language
     * @returns {Object.<string, Object.<string, string>>}
     */
    static initErrorMessages(rules, messages, methods, language) {
        return Object.keys(rules).reduce((names, name) => ({
            ...names,
            [name]: Object.keys(rules[name]).reduce((ruleNames, method) => ({
                ...ruleNames,
                [method]: translate((messages[name] && messages[name][method]) || (methods[method] && methods[method].message) || '', language),
            }), {}),
        }), {});
    }
}

module.exports = JediValidate;

import { translate } from '../i18n/jedi-validate-i18n';

/**
 * Sending request
 * @param {Object} options - Sending options
 * @param {string} options.url
 * @param {string} options.enctype - Sending options
 * @param {string} options.sendType - Sending options
 * @param {string} options.method - Sending options
 * @param {string|FormData} options.data - Sending options
 * @returns {Promise}
 * todo rewrite to fetch
 */
export function ajax(options) {
    return new Promise((resolve, reject) => {
        const xhr = new XMLHttpRequest();

        xhr.open(options.method, options.url + (options.method.toUpperCase() === 'GET' ? (`?${options.data}`) : ''), true);

        if (options.sendType === 'serialize') {
            xhr.setRequestHeader('Content-type', options.enctype);
        } else if (options.sendType === 'json') {
            xhr.setRequestHeader('Content-type', 'application/json; charset=utf-8');
        }

        xhr.onreadystatechange = () => {
            if (xhr.readyState === 4) {
                if (xhr.status === 200) {
                    let response = {};

                    try {
                        response = JSON.parse(xhr.responseText);
                    } catch (e) {
                        response.validationErrors = { base: [translate('JSON parsing error')] }; // todo rewrite translate now dont work
                    }

                    resolve(response);
                } else {
                    reject({
                        xhr,
                        method: options.method,
                        url: options.url,
                        status: xhr.status,
                        statusText: xhr.statusText,
                    });
                }
            }
        };

        xhr.send(options.method.toUpperCase() === 'POST' ? options.data : '');
    });
}

export default ajax;

/**
 * Create part url for serialize method
 * @param {string} name
 * @param {object|Array|string} data
 * @returns {string} - part of url
 */
export function getQueryPart(name, data) {
    if (Array.isArray(data)) {
        return data.reduce((part, index) => part + getQueryPart(`${name}[${index}]`, data[index]), '');
    } else if (typeof data === 'object') {
        return Object.keys(data).reduce((part, index) => part + getQueryPart(`${name}[${index}]`, data[index]), '');
    }

    return `${name}=${encodeURIComponent(data)}&`;
}

/**
 * Name regexp for conversion to path
 * @type {RegExp}
 */
const NAME = /(\[(\w*)\]|\w*)/gi;

/**
 * Convert name of input to path array
 * @param {string} name - name of input
 * @returns {Array} - path to value in data object
 */
export function convertNameToPath(name) {
    const path = [];

    let matches = NAME.exec(name);
    while (matches !== null) {
        if (matches.index === NAME.lastIndex) {
            NAME.lastIndex += 1;
        }

        path.push(matches[2] || matches[1]);

        matches = NAME.exec(name);
    }

    return path;
}

/**
 * Convert data object to value for sending
 * @param {object} data - data object
 * @param {string} type - type of conversion
 * @returns {string|FormData} - output value
 */
export function convertData(data, type) {
    let convertedData;

    switch (type) {
    case 'serialize':
        convertedData = Object.keys(data)
            .reduce((query, name) => `${query}${getQueryPart(name, data[name])}`, '');
        return convertedData.length ? convertedData.slice(0, -1) : '';
    case 'formData':
        return Object.keys(data).reduce((formData, name) => {
            if (data[name] instanceof FileList) {
                if (data[name].length > 1) {
                    for (let i = 0; i < data[name].length; i += 1) {
                        formData.append(`${name}[${i}]`, data[name][i]);
                    }
                } else if (data[name].length === 1) {
                    formData.append(name, data[name][0]);
                }
            } else if (typeof data[name] === 'object') {
                Object.keys(data[name]).forEach(key => formData.append(`${name}[${key}]`, data[name][key]));
            } else {
                formData.append(name, data[name]);
            }

            return formData;
        }, new FormData());
    case 'json':
    default:
        return JSON.stringify(data);
    }
}

import deepmerge from 'deepmerge';

import { convertNameToPath } from './convert-data';

/**
 * Create object by path and value
 * @param {Array} path - path array
 * @param {string|FileList} value - value on input
 * @returns {object} - data object
 */
export function createObject(path, value) {
    const segment = path[0];

    if (segment.length === 0) {
        return value;
    } else if (segment === '[]') {
        return [createObject(path.slice(1), value)];
    }

    return { [segment]: createObject(path.slice(1), value) };
}

/**
 * Get value from data object by path
 * @param {Array} path - value path
 * @param {object} data - data object
 * @returns {string} - value
 */
export function getValueByPath(path, data) {
    return path.reduce((value, segment) => (segment && value ? value[segment] : value), data || '');
}

/**
 * Get value from data object by name
 * @param {string} name - input name
 * @param {object} data - data object
 */
export function getValueByName(name, data) {
    const path = convertNameToPath(name);
    return getValueByPath(path, data);
}

/**
 * Get value from radio group
 * @param {Array} inputs - array of radio inputs
 * @returns {string|Array.<string>} - value of checked input
 */
export function getRadioGroupValue(inputs) {
    const values = [...inputs].map(radio => getInputValue(radio)).filter(Boolean); // eslint-disable-line no-use-before-define, max-len

    return values.length > 1 ? values : values[0];
}

/**
 * Get value form input
 * @param {HTMLInputElement|HTMLSelectElement|Array} input - input
 * @returns {string|FileList|Array} - value of input, or array of value if input is select
 */
export function getInputValue(input) {
    if (!input) return '';

    const { type } = input;

    if (Array.isArray(input)) {
        return getRadioGroupValue(input);
    }

    switch (type) {
    case 'select-one':
        return input.options && input.options[input.selectedIndex] ? input.options[input.selectedIndex].value : '';
    case 'select-multiple':
        return [...input.options].filter(option => option.selected).map(option => option.value);
    case 'checkbox':
    case 'radio':
        return input.checked ? input.value : '';
    case 'file':
        return input.files;
    default:
        return input.value;
    }
}

/**
 * Get name from input or array of inputs
 * @param {HTMLInputElement|Array} input - input element or Array of HTMLInputElements
 * @returns {string} - input name
 */
export function getInputName(input) {
    return Array.isArray(input) ? input[0].name : input.name;
}

/**
 * Get object which key is name of input and value is value of input
 * @param {HTMLInputElement|Array} input - input element or Array of HTMLInputElements
 * @returns {object} - data
 */
export function getInputData(input) {
    const value = getInputValue(input);
    const name = getInputName(input);
    const path = convertNameToPath(name);

    return createObject(path, value);
}

/**
 * Get data object with values from inputs object
 * @param {object} inputs - inputs object
 * @returns {object} - data object
 */
export function getData(inputs) {
    return Object.keys(inputs).reduce(
        (data, name) => deepmerge(data, getInputData(inputs[name])),
        {},
    );
}

/**
 * Get ajax options from form
 * @param {HTMLFormElement} form - form element
 * @returns {{ajax: {url: string, method: string, enctype: string, sendType: *}}} - options
 */
export function getFormOptions(form) {
    const enctype = form.getAttribute('enctype');

    return {
        ajax: {
            enctype,
            url: form.getAttribute('action'),
            method: form.getAttribute('method'),
            sendType: enctype === 'multipart/form-data' ? 'formData' : undefined,
        },
    };
}

/**
 * Get validate options from input attribute of className
 * @param {HTMLInputElement|HTMLSelectElement} input - input for validation
 * @returns {object} - rules
 */
export function getInputRules(input) {
    const defaultRules = ['required', 'email', 'tel', 'url'];

    const rules = defaultRules.reduce((inputRules, rule) => ({
        ...inputRules,
        [rule]: input.hasAttribute(rule) || input.type === rule || input.classList.contains(rule),
    }), {});

    return {
        ...rules,
        regexp: input.hasAttribute('pattern') ? new RegExp(input.getAttribute('pattern')) : undefined,
    };
}

export default {
    required: {
        func: (value) => {
            if (!value) return false;
            if (Array.isArray(value) && value.length === 0) return false;
            if (value instanceof FileList && value.length === 0) return false;
            if (typeof value === 'string' && value.trim() === '') return false;

            return true;
        },
        message: 'This field is required',
    },
    regexp: {
        func: (value, regexp) => regexp.test(value),
        message: 'Please, provide correct value',
    },
    email: {
        func: value => /[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}/i.test(value),
        message: 'This email is incorrect',
    },
    filesize: {
        func: (value, size) => [...value].reduce((r, file) => file.size < size && r, true), // eslint-disable-line max-len
        message: 'This file is too large',
    },
    extension: {
        func: (value, extensions) => [...value].reduce((r, file) => extensions.indexOf(file.name.split('.').pop()) !== -1 && r, true), // eslint-disable-line max-len
        message: 'This extension is not supported',
    },
    tel: {
        func: value => /^([+]+)*[0-9\x20\x28\x29-]{5,20}$/.test(value),
        message: 'This phone number is incorrect',
    },
    url: {
        func: value => /[-a-zA-Z0-9@:%_+.~#?&//=]{2,256}\.[a-z]{2,4}\b(\/[-a-zA-Z0-9@:%_+.~#?&//=]*)?/gi.test(value), // eslint-disable-line max-len
        message: 'Wrong url',
    },
};

import { getValueByName } from './get-data';

/**
 * Check rule for dependencies
 * @param {*} params - params of validation
 * @param {object} data - data of form
 * @returns {*} - params of validation or null if rule not checkable
 */
export function isCheckable(params, data) {
    if (!params) return null;

    let checkable = true;
    let param = params;

    if (Array.isArray(params)) {
        let dependencies = [];
        [param, ...dependencies] = params;
        if (!param) return null;

        try {
            checkable = dependencies
                .reduce((required, dependency) => (required && ((typeof dependency === 'function' && dependency(data)) || !!data[dependency])), checkable);
        } catch (e) {
            console.warn(`Dependency function error: ${e.toString()}`);
        }
    }

    return checkable ? param : null;
}

/**
 * Validate field
 * @param {object} rules - object with rules for validation
 * @param {object} methods - validation methods
 * @param {string|FileList|Array} value - value of input
 * @param {string} name - name on input
 * @param {object} errorMessages - object with error messages
 * @param {object} data - data of form
 * @returns {Array.<string>} - array of field errors
 */
export function validateField(rules, methods, value, name, errorMessages, data) {
    const isEmpty = !methods.required.func(value);

    const isRequired = isCheckable(rules.required, data);

    if (isEmpty && isRequired) {
        return [errorMessages[name].required];
    }

    if (isEmpty) {
        return [];
    }

    return Object.keys(rules).reduce((errors, method) => {
        const params = isCheckable(rules[method], data);

        if (params === null) return errors;

        if (methods[method]) {
            const valid = methods[method].func(value, params);

            if (!valid) {
                errors.push(errorMessages[name][method]);
            }
        } else {
            errors.push(`Method "${method}" not found`);
        }

        return errors;
    }, []);
}

/**
 * Validate data object
 * @param {object} rules - object with rules for validation
 * @param {object} methods - validation methods
 * @param {object} data - data object
 * @param {object} errorMessages - object with error messages
 * @returns {object.<string, Array.<string>>} - object of fields error arrays
 */
export function validateData(rules, methods, data, errorMessages) {
    return Object.keys(rules).reduce((obj, name) => {
        const value = getValueByName(name, data);
        const errors = validateField(rules[name], methods, value, name, errorMessages, data);
        return {
            ...obj,
            [name]: errors.length ? errors : undefined,
        };
    }, {});
}
